mapper.select:
select 

from RECORD_PER_DAY_STATISTICS
where RECORD_DATE = #{recordDate,jdbcType=DATE}
  and MAC_MACHINE_NO = #{macMachineNo,jdbcType=VARCHAR}
  and COMPRESSION = #{compression,jdbcType=BIT}

select
  COALESCE(SUM(${column}),0)
from RECORD_PER_DAY_STATISTICS


    AND RECORD_DATE = ${recordDate}


    AND COMPRESSION = ${compression}

select MAC_MACHINE_NO as macMachineNo, SUM(MAC_COUNT) as count from RECORD_PER_DAY_STATISTICS
COMPRESSION = 0
GROUP BY MAC_MACHINE_NO ORDER BY count ASC limit 0,#{num}

mapper.delete:
delete from RECORD_PER_DAY_STATISTICS
where RECORD_DATE = #{recordDate,jdbcType=DATE}
  and MAC_MACHINE_NO = #{macMachineNo,jdbcType=VARCHAR}
  and COMPRESSION = #{compression,jdbcType=BIT}
